import React, { useState } from 'react';
import { useShoppingCart } from '../contexts/ShoppingCartContext';
import { showConfirmation } from '../utils/notifications';

const formatPrice = (price) => {
  return price.toFixed(2).replace('.', ',');
};

const ShoppingCart = () => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity, 
    clearCart, 
    getTotalPrice 
  } = useShoppingCart();

  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [paymentData, setPaymentData] = useState({
    name: '',
    email: '',
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    address: ''
  });
  const [paymentStep, setPaymentStep] = useState(1); // 1: datos, 2: procesando, 3: confirmaciÃ³n

  const handleRemoveItem = (productId) => {
    removeFromCart(productId);
  };

  const handleQuantityChange = (productId, newQuantity) => {
    updateQuantity(productId, newQuantity);
  };

  const handleClearCart = () => {
    const totalItems = cartItems.reduce((acc, item) => acc + item.quantity, 0);
    showConfirmation(
      'Â¿EstÃ¡s seguro?',
      `Se van a borrar ${totalItems} productos.`,
      () => {
        clearCart();
      }
    );
  };

  const handlePurchase = () => {
    // Simular pasarela de pago
    const totalAmount = getTotalPrice();
    const itemCount = cartItems.reduce((acc, item) => acc + item.quantity, 0);
    
    // Mostrar informaciÃ³n de la compra simulada
    alert(`ðŸ›’ SimulaciÃ³n de Pasarela de Pago\n\n` +
          `ðŸ“¦ ArtÃ­culos: ${itemCount}\n` +
          `ðŸ’° Total: ${totalAmount.toFixed(2).replace('.', ',')} â‚¬\n\n` +
          `âœ… Esta es una demostraciÃ³n de portfolio.\n` +
          `En una tienda real, aquÃ­ aparecerÃ­a la pasarela de pago.`);
    
    clearCart();
  };

  if (cartItems.length === 0) {
    return (
      <main className="main-content">
        <div className="cart-container">
          <div className="empty-cart">
            <div className="empty-cart-icon">
              <svg viewBox="0 0 100 100" fill="none" xmlns="http://www.w3.org/2000/svg">
                {/* Cesta */}
                <path d="M20 30 L20 80 C20 85 24 90 30 90 L70 90 C76 90 80 85 80 80 L80 30" stroke="#000" strokeWidth="3" fill="none"/>
                <path d="M15 30 L85 30" stroke="#000" strokeWidth="3"/>
                <path d="M25 30 L25 20 C25 15 30 10 35 10 L65 10 C70 10 75 15 75 20 L75 30" stroke="#000" strokeWidth="3" fill="none"/>
                
                {/* Cara triste */}
                <circle cx="35" cy="50" r="3" fill="#dc3545"/>
                <circle cx="65" cy="50" r="3" fill="#dc3545"/>
                <path d="M40 70 Q50 60 60 70" stroke="#dc3545" strokeWidth="2" fill="none"/>
              </svg>
            </div>
            <h3 className="empty-cart-title">Carrito vacÃ­o</h3>
            <p className="empty-cart-message">No tienes ningÃºn artÃ­culo en tu carrito.</p>
          </div>
        </div>
      </main>
    );
  }

  return (
    <main className="main-content">
      <h2 className="main-title">Carrito</h2>
      <div className="cart-container">
        <div className="cart-items">
          {cartItems.map(item => (
            <div key={item.id} className="cart-item">
              <img 
                className="cart-item-image" 
                src={item.image} 
                alt={item.name}
              />
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <button 
                  className="remove-item-text"
                  onClick={() => handleRemoveItem(item.id)}
                >
                  Quitar artÃ­culo
                </button>
              </div>
              <div className="cart-item-quantity">
                <small>Cantidad</small>
                <div className="quantity-controls">
                  <button 
                    onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                    className="quantity-btn"
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button 
                    onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                    className="quantity-btn"
                  >
                    +
                  </button>
                </div>
              </div>
              <div className="cart-item-price">
                <small>Precio</small>
                <p>{formatPrice(item.price)} â‚¬</p>
              </div>
              <div className="cart-item-subtotal">
                <small>Subtotal</small>
                <p>{formatPrice(item.price * item.quantity)} â‚¬</p>
              </div>
              <button 
                className="cart-item-remove"
                onClick={() => handleRemoveItem(item.id)}
              >
                <i className="bi bi-trash-fill"></i>
              </button>
            </div>
          ))}
        </div>

        <div className="cart-actions">
          <div className="cart-actions-left">
            <button 
              className="clear-cart-button"
              onClick={handleClearCart}
            >
              Vaciar carrito
            </button>
          </div>
          <div className="cart-actions-right">
            <div className="cart-total">
              <p>Total:</p>
              <p>{formatPrice(getTotalPrice())} â‚¬</p>
            </div>
            <button 
              className="purchase-button"
              onClick={handlePurchase}
            >
              Comprar ahora
            </button>
          </div>
        </div>
      </div>
    </main>
  );
};

export default ShoppingCart;
