import React, { createContext, useContext, useState, useEffect } from 'react';
import { productsData } from '../data/products';

const ProductContext = createContext();

export const useProducts = () => {
  const context = useContext(ProductContext);
  if (!context) {
    throw new Error('useProducts must be used within a ProductProvider');
  }
  return context;
};

export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [categories, setCategories] = useState([]);

  // Cargar productos desde la API
  const loadProducts = async (params = {}) => {
    try {
      setLoading(true);
      setError(null);
      const response = await productService.getAll(params);
      
      if (response.success) {
        setProducts(response.data);
      } else {
        throw new Error(response.message || 'Error al cargar productos');
      }
    } catch (err) {
      console.error('Error loading products:', err);
      setError(err.message);
      // Fallback a datos locales si la API falla
      setProducts(getLocalProducts());
    } finally {
      setLoading(false);
    }
  };

  // Cargar categorías desde la API
  const loadCategories = async () => {
    try {
      const response = await productService.getCategories();
      if (response.success) {
        setCategories(response.data);
      }
    } catch (err) {
      console.error('Error loading categories:', err);
      // Fallback a categorías locales
      setCategories([
        { id: 'botas', name: 'Botas' },
        { id: 'zapatillas', name: 'Zapatillas' }
      ]);
    }
  };

  // Buscar productos
  const searchProducts = async (query) => {
    try {
      setLoading(true);
      const response = await productService.search(query);
      
      if (response.success) {
        setProducts(response.data);
      } else {
        throw new Error(response.message || 'Error en la búsqueda');
      }
    } catch (err) {
      console.error('Error searching products:', err);
      setError(err.message);
      // Fallback a búsqueda local
      const filteredProducts = getLocalProducts().filter(product =>
        product.name.toLowerCase().includes(query.toLowerCase()) ||
        product.description.toLowerCase().includes(query.toLowerCase())
      );
      setProducts(filteredProducts);
    } finally {
      setLoading(false);
    }
  };

  // Obtener productos por categoría
  const getProductsByCategory = async (category) => {
    try {
      setLoading(true);
      const response = await productService.getByCategory(category);
      
      if (response.success) {
        setProducts(response.data);
      } else {
        throw new Error(response.message || 'Error al cargar productos de la categoría');
      }
    } catch (err) {
      console.error('Error loading category products:', err);
      setError(err.message);
      // Fallback a datos locales
      const filteredProducts = getLocalProducts().filter(product =>
        product.category.toLowerCase() === category.toLowerCase()
      );
      setProducts(filteredProducts);
    } finally {
      setLoading(false);
    }
  };

  // Obtener producto por ID
  const getProductById = async (id) => {
    try {
      const response = await productService.getById(id);
      if (response.success) {
        return response.data;
      } else {
        throw new Error(response.message || 'Producto no encontrado');
      }
    } catch (err) {
      console.error('Error loading product:', err);
      // Fallback a datos locales
      return getLocalProducts().find(product => product.id === parseInt(id));
    }
  };

  // Datos locales como fallback
  const getLocalProducts = () => {
    return [
      // Botas
      { id: 1, name: 'Botas de Montaña', description: 'Botas resistentes para senderismo y montaña con suela antideslizante.', price: 89.99, image: '/img/botas/img01.png', category: 'Botas', stock: 15 },
      { id: 2, name: 'Botas Casuales', description: 'Botas elegantes para uso diario con diseño moderno.', price: 65.50, image: '/img/botas/img02.png', category: 'Botas', stock: 20 },
      { id: 3, name: 'Botas de Trabajo', description: 'Botas resistentes para trabajo pesado con protección reforzada.', price: 120.00, image: '/img/botas/img03.png', category: 'Botas', stock: 8 },
      { id: 4, name: 'Botas de Cuero', description: 'Botas de cuero genuino con acabado premium.', price: 150.75, image: '/img/botas/img04.png', category: 'Botas', stock: 12 },
      { id: 5, name: 'Botas Deportivas', description: 'Botas ligeras para actividades deportivas.', price: 75.25, image: '/img/botas/img05.png', category: 'Botas', stock: 18 },
      { id: 6, name: 'Botas de Invierno', description: 'Botas térmicas para climas fríos con aislamiento.', price: 95.80, image: '/img/botas/img06.png', category: 'Botas', stock: 10 },
      { id: 7, name: 'Botas Urbanas', description: 'Botas modernas para el día a día en la ciudad.', price: 68.90, image: '/img/botas/img07.png', category: 'Botas', stock: 25 },
      { id: 8, name: 'Botas de Seguridad', description: 'Botas con puntera de acero para máxima protección.', price: 110.45, image: '/img/botas/img08.png', category: 'Botas', stock: 6 },
      
      // Zapatillas
      { id: 9, name: 'Zapatillas Running', description: 'Zapatillas ligeras para correr con tecnología de amortiguación.', price: 85.99, image: '/img/zapatillas/img09.png', category: 'Zapatillas', stock: 30 },
      { id: 10, name: 'Zapatillas Casual', description: 'Zapatillas cómodas para uso diario con estilo urbano.', price: 55.50, image: '/img/zapatillas/img10.png', category: 'Zapatillas', stock: 40 },
      { id: 11, name: 'Zapatillas Deportivas', description: 'Zapatillas versátiles para múltiples deportes.', price: 70.25, image: '/img/zapatillas/img11.png', category: 'Zapatillas', stock: 35 },
      { id: 12, name: 'Zapatillas de Baloncesto', description: 'Zapatillas especializadas para baloncesto con máximo agarre.', price: 125.00, image: '/img/zapatillas/img12.png', category: 'Zapatillas', stock: 15 },
      { id: 13, name: 'Zapatillas de Fútbol', description: 'Zapatillas con tacos para fútbol en césped natural.', price: 90.75, image: '/img/zapatillas/img13.png', category: 'Zapatillas', stock: 20 },
      { id: 14, name: 'Zapatillas Minimalistas', description: 'Zapatillas ligeras con diseño minimalista.', price: 60.80, image: '/img/zapatillas/img14.png', category: 'Zapatillas', stock: 28 },
      { id: 15, name: 'Zapatillas de Skate', description: 'Zapatillas resistentes para skateboarding.', price: 75.45, image: '/img/zapatillas/img15.png', category: 'Zapatillas', stock: 22 },
      { id: 16, name: 'Zapatillas de Trail', description: 'Zapatillas para senderismo con suela antideslizante.', price: 95.60, image: '/img/zapatillas/img16.png', category: 'Zapatillas', stock: 18 }
    ];
  };

  // Cargar datos iniciales
  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  const value = {
    products,
    categories,
    loading,
    error,
    loadProducts,
    searchProducts,
    getProductsByCategory,
    getProductById,
    loadCategories
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};

export default ProductContext;
