import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useShoppingCart } from '../hooks/useShoppingCart';

const Navigation = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { getTotalItems } = useShoppingCart();
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <>
      {/* Mobile Header */}
      <header className="mobile-header">
        <h1 className="logo">FOOTLILY</h1>
        <button className="open-menu" onClick={toggleMenu}>
          <i className="bi bi-list"></i>
        </button>
      </header>

      {/* Sidebar Navigation */}
      <aside className={`sidebar ${isMenuOpen ? 'sidebar-visible' : ''}`}>
        <button className="close-menu" onClick={closeMenu}>
          <i className="bi bi-x"></i>
        </button>
        
        <header>
          <h1 className="logo">FOOTLILY</h1>
        </header>
        
        <nav>
          <ul className="menu">
            <li>
              <Link 
                to="/" 
                className={`menu-button category-button ${isActive('/') ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <i className="bi bi-hand-index-thumb-fill"></i>
                Todos los productos
              </Link>
            </li>
            <li>
              <Link 
                to="/category/boots" 
                className={`menu-button category-button ${isActive('/category/boots') ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <i className="bi bi-hand-index-thumb"></i>
                Botas
              </Link>
            </li>
            <li>
              <Link 
                to="/category/sneakers" 
                className={`menu-button category-button ${isActive('/category/sneakers') ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <i className="bi bi-hand-index-thumb"></i>
                Zapatillas
              </Link>
            </li>
            <li>
              <Link 
                to="/cart" 
                className={`menu-button cart-button ${isActive('/cart') ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <i className="bi bi-cart-fill"></i>
                Carrito 
                <span className="cart-count">{getTotalItems()}</span>
              </Link>
            </li>
          </ul>
        </nav>

        <footer>
          <p className="footer-text">Â© 2024 FootLily</p>
        </footer>
      </aside>
    </>
  );
};

export default Navigation;
